function onLoad() {
  let allThemes = [];
  let filteredThemes = [];

  function smartNumber(input) {
    if (!input) return NaN;

    // extract "34k", "1.2M", "999", etc.
    const match = input.match(/^\s*([\d.,]+(?:\.\d+)?\s*[km]?)/i);
    if (!match) return NaN;

    const s = match[1].toLowerCase().replace(/,/g, '').trim();

    if (s.endsWith('k')) return parseFloat(s) * 1000;
    if (s.endsWith('m')) return parseFloat(s) * 1000000;

    return parseFloat(s);
  }

  function parseHTML() {
    const htmlInput =
      initialState.htmlString || document.getElementById('htmlInput').value;
    const message = document.getElementById('message');
    const themeList = document.getElementById('themeList');

    if (!htmlInput.trim()) {
      message.className = 'error';
      message.innerHTML =
        '<strong>Error:</strong> Please paste the HTML source code first!';
      message.style.display = 'block';
      return;
    }

    try {
      const parser = new DOMParser();
      const doc = parser.parseFromString(htmlInput, 'text/html');

      const packages = doc.querySelectorAll('li.package');
      allThemes = [];

      if (packages.length === 0) {
        message.className = 'error';
        message.innerHTML =
          '<strong>Error:</strong> No packages found. Make sure you copied the full HTML source.';
        message.style.display = 'block';
        return;
      }

      packages.forEach((pkg) => {
        const titleLink = pkg.querySelector('h3 a');
        const description = pkg.querySelector('p.description');
        const installsSpan = pkg.querySelector('span.meta span.installs');

        if (titleLink) {
          const title = titleLink.textContent.trim();
          const url =
            'https://packagecontrol.io' + titleLink.getAttribute('href');
          const desc = description
            ? description.textContent.trim()
            : 'No description';
          const installsText = installsSpan ? installsSpan.textContent : '0';
          const installs = smartNumber(installsText);
          console.log(`in2stalls of ${title}: ${installsText} - ${installs}`);
          allThemes.push({
            title,
            url,
            description: desc,
            installs,
          });
        }
      });

      // Sort by installs (descending)
      allThemes.sort((a, b) => b.installs - a.installs);
      filteredThemes = [...allThemes];

      message.className = 'success';
      message.innerHTML = `<strong>Success!</strong> Found ${allThemes.length} themes. Sorted by install count.`;
      message.style.display = 'block';

      displayThemes();
      updateStats();
      document.getElementById('stats').style.display = 'flex';
    } catch (err) {
      message.className = 'error';
      message.innerHTML = `<strong>Error:</strong> ${err.message}`;
      message.style.display = 'block';
    }
  }

  function displayThemes() {
    const themeList = document.getElementById('themeList');
    themeList.innerHTML = '';

    if (filteredThemes.length === 0) {
      themeList.innerHTML =
        '<p style="text-align: center; color: #999; padding: 40px;">No themes match your search.</p>';
      return;
    }

    filteredThemes.forEach((theme, index) => {
      const card = document.createElement('div');
      card.className = 'theme-card';
      card.onclick = () => window.open(theme.url, '_blank');

      card.innerHTML = `
                <div class="theme-title">
                    <span class="rank-badge">#${
                      allThemes.indexOf(theme) + 1
                    }</span>
                    ${theme.title}
                </div>
                <div class="theme-description">${theme.description}</div>
                <div class="theme-stats">
                    <span>
                        <span class="installs">${theme.installs.toLocaleString()}</span>
                        <span class="installs-label">installs</span>
                    </span>
                    <a href="${
                      theme.url
                    }" class="theme-link" onclick="event.stopPropagation()">View Package →</a>
                </div>
            `;

      themeList.appendChild(card);
    });
  }

  function filterThemes() {
    const searchTerm = document.getElementById('searchBox').value.toLowerCase();

    if (searchTerm === '') {
      filteredThemes = [...allThemes];
    } else {
      filteredThemes = allThemes.filter(
        (theme) =>
          theme.title.toLowerCase().includes(searchTerm) ||
          theme.description.toLowerCase().includes(searchTerm)
      );
    }

    displayThemes();
  }

  function updateStats() {
    const totalInstalls = allThemes.reduce(
      (sum, theme) => sum + theme.installs,
      0
    );
    const avgInstalls = Math.round(totalInstalls / allThemes.length);

    document.getElementById('totalThemes').textContent = allThemes.length;
    document.getElementById('totalInstalls').textContent =
      totalInstalls.toLocaleString();
    document.getElementById('avgInstalls').textContent =
      avgInstalls.toLocaleString();
  }

  function exportData() {
    if (allThemes.length === 0) {
      alert('Please parse themes first!');
      return;
    }

    const dataStr = JSON.stringify(allThemes, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement('a');
    link.href = url;
    link.download = 'packagecontrol-themes.json';
    link.click();
    URL.revokeObjectURL(url);
  }

  window.parseHTML = parseHTML;
  window.smartNumber = smartNumber;
}

window.addEventListener('load', onLoad);
